mapper.select:

select fno, userid, message, link, regdate, updatedate, replycnt
from (
	select /*+INDEX_DESC(feed pk_feed)*/
		rownum rn, fno, userid, message, link, regdate, updatedate, replycnt
		from feed
		where


		
		rownum <= #{pageNum} * #{amount}
	)
where rn > (#{pageNum} -1) * #{amount}
