mapper.select:

	select rno, fno, reply, replyer, replydate, updatedate
	from (
			select /*+INDEX_DESC(feed_reply idx_reply)*/ 
			rownum rn, rno, fno, reply, replyer, replydate, updatedate
			from feed_reply
			where fno = #{fno}
			and rno > 0
			and rownum <= #{cri.pageNum} * #{cri.amount}
		)
	where rn > (#{cri.pageNum} -1) * #{cri.amount}

select * from feed_reply 
where fno = #{fno} 
and replydate = (select max(replydate) from feed_reply)
